import { useSelector, useDispatch } from "react-redux";
import { pagenationActions } from "../../store/pagenationSlice";
import styles from "./Pagenation.module.css";

const paging = (totalResults, currentPage, segments) => {
  let pages = Math.ceil(totalResults / 6);
  let first = false;
  let last = false;
  let tempLength = 0;

  const space = [];
  if (currentPage === 1 || currentPage === 2 || currentPage === 3) {
    if (segments.length < 6) {
      tempLength = segments.length;
    } else {
      tempLength = 5;
    }
    for (let i = 0; i < tempLength; i++) {
      space.push(i + 1);
    }
  } else if (
    currentPage === pages ||
    currentPage === pages - 1 ||
    currentPage === pages - 2
  ) {
    for (let i = 4; i >= 0; i++) {
      space.push(pages - i);
    }
  } else {
    space.push(currentPage - 2);
    space.push(currentPage - 1);
    space.push(currentPage);
    space.push(currentPage + 1);
    space.push(currentPage + 2);
  }

  if (currentPage === 1) {
    first = true;
  }

  if (currentPage === pages) {
    last = true;
  }

  return { space, first, last };
};

const Pagenation = ({ totalResults, segments }) => {
  const currentPage = useSelector((state) => state.pagenation.currentPage);
  const dispatch = useDispatch();
  const { space, first, last } = paging(totalResults, currentPage, segments);

  return (
    <>
      <ul
        className={`d-flex justify-content-end gap-4 ${styles["pagenation-el"]}`}
      >
        <li className={`p-3 ${first === true ? styles["hidden"] : ""}`}>
          <button
            className="page-link"
            href="#"
            id="btn-prev"
            onClick={() =>
              dispatch(pagenationActions.setCurrentPage(currentPage - 1))
            }
          >
            Previous
          </button>
        </li>

        {space.map((s, i) => (
          <li
            key={segments[i]}
            className={`p-3 ${
              space[i] === currentPage ? styles["disable"] : ""
            }`}
          >
            <button
              className="page-link"
              id="page-num"
              onClick={() =>
                dispatch(pagenationActions.setCurrentPage(space[i]))
              }
            >
              {space[i]}
            </button>
          </li>
        ))}

        <li className={`p-3 ${last === true ? styles["hidden"] : ""}`}>
          <button
            className="page-link"
            id="btn-next"
            onClick={() =>
              dispatch(pagenationActions.setCurrentPage(currentPage + 1))
            }
          >
            Next
          </button>
        </li>
      </ul>
    </>
  );
};

export default Pagenation;
