import { useRouteLoaderData } from "react-router-dom";
import ProductItem from "./ProductItem";

const ProductsList = () => {
  const productsList = useRouteLoaderData("home");
  const firstFourItems = productsList.filter((product, index) => {
    if (index >= 0 && index <= 3) {
      return product;
    }
  });

  console.log(firstFourItems);

  const nextFourItems = productsList.filter((product, index) => {
    if (index >= 4 && index <= 7) {
      return product;
    }
  });

  console.log(`Second : ${nextFourItems}`);

  return (
    <>
      <div className="container">
        <div className="d-flex flex-column align-items-center">
          <h2>Made the hard way</h2>
          <h3>Top trending products</h3>
        </div>
        <div className="d-flex ">
          {firstFourItems &&
            firstFourItems.map((product, index) => (
              <ProductItem key={product._id} product={product} />
            ))}
        </div>
        <div className="d-flex">
          {nextFourItems &&
            nextFourItems.map((product, index) => (
              <ProductItem key={product._id} product={product} />
            ))}
        </div>
      </div>
    </>
  );
};

export default ProductsList;
