          <form className="input-group" onSubmit={clientChatHandler}>
            <input
              type="text"
              className="form-control"
              name="message"
              placeholder="Enter your message..."
            />
            <button className="btn btn-warning">Send</button>
          </form>
        </div>
      </Card>
    </>
  );
};

export default LiveChatPopup;
