import styles from "./Popup.module.css";
import { Link, useRouteLoaderData } from "react-router-dom";
import { useSelector } from "react-redux";
import { useEffect, useState } from "react";

const Popup = () => {
  const productId = useSelector((state) => state.popup.productId);
  const data = useRouteLoaderData("home");
  const [currentProduct, setCurrentProduct] = useState({});
  console.log(currentProduct);

  useEffect(() => {
    setCurrentProduct(
      data.filter((product) => {
        if (product["_id"]["$oid"] === productId) {
          return product;
        }
      })[0]
    );
  }, [productId]);

  return (
    <>
      {/* <div  */}
      {currentProduct && (
        <>
          <div className={styles["overlay"]}></div>
          <div className={`p-5 ${styles["current-product"]}`}>
            <div className="d-flex">
              <div>
                <img
                  src={currentProduct.img1}
                  alt="Product detail"
                  className="img-fluid"
                />
              </div>
              <div>
                <p>{currentProduct.name}</p>
                <p>{currentProduct.price}</p>
                <p>{currentProduct.short_desc}</p>
                <Link to="/shop">
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    width="16"
                    height="16"
                    fill="currentColor"
                    className={`bi bi-cart3 ${styles["icon"]}`}
                    viewBox="0 0 16 16"
                  >
                    <path d="M0 1.5A.5.5 0 0 1 .5 1H2a.5.5 0 0 1 .485.379L2.89 3H14.5a.5.5 0 0 1 .49.598l-1 5a.5.5 0 0 1-.465.401l-9.397.472L4.415 11H13a.5.5 0 0 1 0 1H4a.5.5 0 0 1-.491-.408L2.01 3.607 1.61 2H.5a.5.5 0 0 1-.5-.5M3.102 4l.84 4.479 9.144-.459L13.89 4zM5 12a2 2 0 1 0 0 4 2 2 0 0 0 0-4m7 0a2 2 0 1 0 0 4 2 2 0 0 0 0-4m-7 1a1 1 0 1 1 0 2 1 1 0 0 1 0-2m7 0a1 1 0 1 1 0 2 1 1 0 0 1 0-2" />
                  </svg>{" "}
                  View Details
                </Link>
              </div>
            </div>
          </div>
        </>
      )}
    </>
  );
};

export default Popup;
