import { useEffect, memo } from "react";
import ProductItem from "./ProductItem";

const ShopProductsList = memo(({ products }) => {
  const firstSixItems = products.filter(
    (product, index) => index >= 0 && index <= 5
  );

  const firstLineItems = firstSixItems.filter(
    (product, index) => index >= 0 && index <= 2
  );

  const secondLineItems = firstSixItems.filter(
    (product, index) => index >= 3 && index <= 5
  );

  return (
    <>
      {firstSixItems && (
        <div className="d-flex flex-column">
          <div className="d-flex">
            {firstLineItems.map((product) => (
              <ProductItem key={product["_id"]["$oid"]} product={product} />
            ))}
          </div>
          <div className="d-flex">
            {secondLineItems.map((product) => (
              <ProductItem key={product["_id"]["$oid"]} product={product} />
            ))}
          </div>
        </div>
      )}
    </>
  );
});

export default ShopProductsList;

// Pagenation function
function pagenation(data) {
  let pages = Math.ceil(data.totalResults / 6);

  pagenationEl.innerHTML = "";
  let first = false;
  let last = false;

  let space = [];
  if (currentPage === 1 || currentPage === 2 || currentPage === 3) {
    space = [1, 2, 3, 4, 5];
  } else if (
    currentPage === pages ||
    currentPage === pages - 1 ||
    currentPage === pages - 2
  ) {
    space = [pages - 4, pages - 3, pages - 2, pages - 1, pages];
  } else {
    space = [
      currentPage - 2,
      currentPage - 1,
      currentPage,
      currentPage + 1,
      currentPage + 2,
    ];
  }

  if (currentPage === 1) {
    first = true;
  }

  if (currentPage === pages) {
    last = true;
  }
  // Previous button, if it true, add hidden class to hide the button

  return (
    <>
      <li className="page-item ${first === true ? 'hidden' : ''}">
        <button
          className="page-link"
          href="#"
          id="btn-prev"
          onclick="clickBtnPage(true)"
        >
          Previous
        </button>
      </li>
      <li className={`page-item ${space[i] === currentPage ? "disabled" : ""}`}>
        <a className="page-link" id="page-num">
          {space[i]}
        </a>
      </li>
      <li className={`page-item ${last === true ? "hidden" : ""}`}>
        <button
          className="page-link"
          id="btn-next"
          onclick="clickBtnPage(false)"
        >
          Next
        </button>
      </li>
    </>
  );
  let html = `
   <li class="page-item ${first === true ? "hidden" : ""}">
     <button class="page-link" href="#" id="btn-prev" onclick="clickBtnPage(true)">Previous</button>
   </li>`;

  // Loop over space array and render the current page with the space is 4 page nearest of it
  for (let i = 0; i < space.length; i++) {
    html += ``;
  }

  // Next button, same as previous button
  html += `
   <li class="page-item ${last === true ? "hidden" : ""}">
     <button class="page-link" id="btn-next" onclick="clickBtnPage(false)">Next</button>
   </li>
   <div>
     <p class="current-page-display">Page ${currentPage} / ${
    data.totalResults
  }</p
   </div>`;
  pagenationEl.insertAdjacentHTML("afterbegin", html);
}
