import { useEffect, useState } from "react";
import CategoriesSideBar from "../navigation/CategoriesSideBar";
import SearchBar from "../search-bar/SearchBar";
import ShopProductsList from "./ShopProductsList";
import { useSelector } from "react-redux";
import { useRouteLoaderData } from "react-router-dom";

const ShopProducts = () => {
  const data = useRouteLoaderData("root");
  const categoryState = useSelector((state) => state.category.category);
  const [productsAfterFilter, setProductsAfterFilter] = useState([...data]);
  // console.log(productsAfterFilter);

  const changeCategory = () => {
    setProductsAfterFilter((prevProducts) => {
      prevProducts.filter((product) => {
        console.log(product.category);
        console.log(categoryState);
        return product.category === categoryState;
      });
      return;
    });
  };

  return (
    <>
      <div className="container d-flex gap-5">
        <CategoriesSideBar onCategoryChange={changeCategory} />
        <div>
          <SearchBar />
          <ShopProductsList products={productsAfterFilter} />
        </div>
      </div>
    </>
  );
};

export default ShopProducts;
