import { useSelector } from "react-redux";

const OrderDetail = () => {
  const listCart = useSelector((state) => state.cart.listCart);

  return (
    <>
      <div>
        <div>
          <h3>Your order</h3>
          {listCart &&
            listCart.map((cart) => (
              <div className="d-flex gap-5 justify-content-between">
                <p>{cart.product.name}</p>
                <p>{Number(cart.product.price).toLocaleString("de-DE")} VND</p>
              </div>
            ))}

          <div className="d-flex gap-5 justify-content-between">
            <p>TOTAL</p>
            <p>
              {listCart.reduce((acc, cart) => {
                acc + cart.total;
              }, 0)}
            </p>
          </div>
        </div>
      </div>
    </>
  );
};

export default OrderDetail;
