import { useSelector } from "react-redux";
import CartItem from "./CartItem";

const CartTable = () => {
  const listCart = useSelector((state) => state.cart.listCart);

  return (
    <>
      <div>
        <h2>Shopping Cart</h2>
        <div>
          <table>
            <thead>
              <tr>
                <th>Image</th>
                <th>Product</th>
                <th>Price</th>
                <th>Quantity</th>
                <th>Total</th>
                <th>Remove</th>
              </tr>
            </thead>
            <tbody>
              {listCart &&
                listCart.map((cart) => {
                  // <CartItem
                  //   key={cartItem.product["_id"]["$oid"]}
                  //   cart={cartItem}
                  // />;
                  <tr>
                    <td>{cart.product.img1}</td>
                    <td>{cart.product.name}</td>
                    <td>{cart.product.price}</td>
                    <td>{cart.quantity}</td>
                    <td>{cart.total}</td>
                    <td>Remove</td>
                  </tr>;
                })}
            </tbody>
          </table>
        </div>
      </div>
    </>
  );
};

export default CartTable;
