import { useParams, useRouteLoaderData } from "react-router-dom";

const DetailPage = () => {
  const params = useParams();
  const productId = params.productId;
  const data = useRouteLoaderData("root");
  const productDetail = data.filter(
    (product) => product["_id"]["$oid"] === productId
  );
  console.log(productDetail);

  return (
    <>
      <div>
        <div>
          <picture>
            <img src={productDetail.img1} alt="Product Detail 1"></img>
            <img src={productDetail.img2} alt="Product Detail 2"></img>
            <img src={productDetail.img3} alt="Product Detail 3"></img>
            <img src={productDetail.img4} alt="Product Detail 4"></img>
          </picture>
          <picture>
            <img src={productDetail.img1} alt="Product Detail 1"></img>
          </picture>
          <div></div>
        </div>
      </div>
    </>
  );
};

export default DetailPage;
