import { useEffect } from "react";
import ProductItem from "./ProductItem";

const ShopProductsList = ({ products }) => {
  const firstSixItems = [];
  const firstLineItems = [];
  const secondLineItems = [];

  useEffect(() => {
    firstSixItems = products.filter(
      (product, index) => index >= 0 && index <= 5
    );

    firstLineItems = firstSixItems.filter(
      (product, index) => index >= 0 && index <= 2
    );

    secondLineItems = firstSixItems.filter(
      (product, index) => index >= 3 && index <= 5
    );
  }, []);

  return (
    <>
      {firstSixItems && (
        <div className="d-flex flex-column">
          <div className="d-flex">
            {firstLineItems.map((product) => (
              <ProductItem key={product["_id"]["$oid"]} product={product} />
            ))}
          </div>
          <div className="d-flex">
            {secondLineItems.map((product) => (
              <ProductItem key={product["_id"]["$oid"]} product={product} />
            ))}
          </div>
        </div>
      )}
    </>
  );
};

export default ShopProductsList;
