import { useRouteLoaderData } from "react-router-dom";
import ProductItem from "./ProductItem";
import styles from "./ProductsList.module.css";

const ProductsList = () => {
  const productsList = useRouteLoaderData("home");
  const firstFourItems = productsList.filter((product, index) => {
    if (index >= 0 && index <= 3) {
      return product;
    }
  });

  const nextFourItems = productsList.filter((product, index) => {
    if (index >= 4 && index <= 7) {
      return product;
    }
  });

  return (
    <>
      <div className={styles["product-list-container"]}>
        <div className="container">
          <div className="d-flex flex-column align-items-start py-3">
            <h2 className={styles["list-header-1"]}>Made the hard way</h2>
            <h3 className={styles["list-header-2"]}>Top trending products</h3>
          </div>
          <div className="d-flex justify-content-between gap-4 text-center">
            {firstFourItems &&
              firstFourItems.map((product) => (
                <ProductItem key={product["_id"]["$oid"]} product={product} />
              ))}
          </div>
          <div className="d-flex justify-content-between gap-4 text-center">
            {nextFourItems &&
              nextFourItems.map((product) => (
                <ProductItem key={product["_id"]["$oid"]} product={product} />
              ))}
          </div>
        </div>
      </div>
    </>
  );
};

export default ProductsList;
