import { configureStore } from "@reduxjs/toolkit";
import popupReducer from "./popupSlice";
import categoryReducer from "./categorySlice";

const store = configureStore({
  reducer: {
    popup: popupReducer,
    category: categoryReducer,
  },
});

export default store;
