import { useParams, useRouteLoaderData } from "react-router-dom";
import styles from "./ProductDetail.module.css";
import { useDispatch } from "react-redux";
import { popupActions } from "../../store/popupSlice";

const ProductDetail = () => {
  const params = useParams();
  const productId = params.productId;
  const data = useRouteLoaderData("root");
  const productDetail = data.filter(
    (product) => product["_id"]["$oid"] === productId
  )[0];

  const str = productDetail.long_desc.split("");
  console.log(productDetail.long_desc);
  const dispatch = useDispatch();
  dispatch(popupActions.hidePopup());

  return (
    <>
      <div className={styles["detail-container"]}>
        <div className="container mb-0">
          <div className={`d-sm-flex py-5 ${styles["detail"]}`}>
            <picture className="d-sm-flex gap-4 flex-column align-items-center">
              <img
                src={productDetail.img1}
                className="image-fuild w-25"
                alt="Product Detail 1"
              ></img>
              <img
                src={productDetail.img2}
                className="image-fuild w-25"
                alt="Product Detail 2"
              ></img>
              <img
                src={productDetail.img3}
                className="image-fuild w-25"
                alt="Product Detail 3"
              ></img>
              <img
                src={productDetail.img4}
                className="image-fuild w-25"
                alt="Product Detail 4"
              ></img>
            </picture>

            <picture>
              <img
                src={productDetail.img1}
                className="w-75 h-100"
                alt="Product Detail 1"
              ></img>
            </picture>

            <div>
              <h2 className="pb-3">{productDetail.name}</h2>
              <p> {Number(productDetail.price).toLocaleString("de-DE")} VND</p>
              <p>{productDetail.short_desc}</p>
              <p>
                Category : <span>{productDetail.category}</span>
              </p>
              <div className="d-sm-flex align-items-center">
                <p className="mb-0 me-2">Quantity :</p>
                <input type="number" placeholder="0" />
                <button className={`py-1 px-3 ms-2 ${styles["btn-add"]}`}>
                  Add to cart
                </button>
              </div>
            </div>
          </div>

          <div>
            <h2 className={styles["description-header"]}>Description</h2>
            <h3 className={styles["description-header-3"]}>
              Product description
            </h3>
            <p className={`mb-0 pb-5 ${styles["long-desc"]}`}>
              {productDetail.long_desc}
            </p>
          </div>
        </div>
      </div>
    </>
  );
};

export default ProductDetail;
