import Card from "../UI/Card";
import styles from "./LiveChatPopup.module.css";
import { useSelector } from "react-redux";

const LiveChatPopup = () => {
  const isDisplay = useSelector((state) => state.liveChat.isDisplay);
  return (
    <>
      <Card
        className={`p-4 ${styles["popup"]} ${!isDisplay && styles["hidden"]}`}
      >
        <div>
          <header className="d-flex gap-5 align-items-center pb-4 border-bottom border-warning">
            <div>
              <p className="mb-0">Customer Support</p>
            </div>
            <div>
              <button className="btn btn-warning">Let's Chat App</button>
            </div>
          </header>

          <main className={styles["main"]}>
            <div>
              <div></div>
              <div>
                <div>
                  <div>BOT</div>
                </div>
                <div>Xin ch√†o</div>
              </div>
            </div>
          </main>

          <form>
            <input type="text" placeholder="Enter your message..." />
            <button>Send</button>
          </form>
        </div>
      </Card>
    </>
  );
};

export default LiveChatPopup;
