import styles from "./Popup.module.css";
import { useRouteLoaderData } from "react-router-dom";
import { useSelector } from "react-redux";
import { useEffect } from "react";

const Popup = () => {
  const productId = useSelector((state) => state.popup.productId);
  const data = useRouteLoaderData("home");
  // console.log(data);
  // console.log(productId);
  let currentProduct = [];

  useEffect(() => {
    currentProduct.push(
      data.filter((product) => {
        if (product["_id"]["$oid"] === productId) {
          return product;
        }
      })
    );
    return () => {
      currentProduct.pop();
    };
  }, [productId]);

  console.log(currentProduct[0]);
  return (
    <>
      {/* <div className={styles["overlay"]}> */}
      <div>
        <div>
          <div>
            <img
              src={currentProduct.img1 ? currentProduct[0].img1 : ""}
              alt="Product detail"
            />
          </div>
        </div>
      </div>
    </>
  );
};

export default Popup;
