import styles from "./BillingDetailForm.module.css";
import { useSelector } from "react-redux";

const BillingDetailForm = () => {
  const listCart = useSelector((state) => state.cart.listCart);
  const total = listCart.reduce((total, cart) => total + cart.total, 0);

  const submitHandler = (event) => {
    event.preventDefault();

    // const billingDetail = {
    //   name: data.get("full-name"),
    //   email: data.get("email"),
    //   phone: data.get("phone"),
    //   address: data.get("address"),
    // };

    // window.alert(
    //   `Your order is done : /nName : ${billingDetail.name} /nEmail : ${billingDetail.email} /nPhone : ${billingDetail.phone} /nAddress : ${billingDetail.address}`
    // );
  };

  return (
    <>
      <div className="flex-fill" onSubmit={submitHandler}>
        <h2 className="pt-3">Billing details</h2>
        <form method="post">
          <div className="form-group pt-4">
            <label className="pb-2">Full name:</label>
            <input
              type="text"
              name="full-name"
              className="form-control"
              placeholder="Enter Your Fullname Here!"
            />
          </div>
          <div className="form-group pt-4">
            <label className="pb-2">Email: </label>
            <input
              type="text"
              name="email"
              className="form-control"
              placeholder="Enter Your Email Here!"
            />
          </div>
          <div className="form-group pt-4">
            <label className="pb-2">Phone number:</label>
            <input
              type="text"
              name="phone"
              className="form-control"
              placeholder="Enter Your Phone Number Here!"
            />
          </div>
          <div className="form-group pt-4">
            <label className="pb-2">Address:</label>
            <input
              type="text"
              name="address"
              className="form-control"
              placeholder="Enter Your Address Here!"
            />
          </div>

          <div className={styles["btn-bill"]}>
            <button type="submit" className="my-4 btn">
              Place order
            </button>
          </div>
        </form>
      </div>
    </>
  );
};

export default BillingDetailForm;
