import { createSlice } from "@reduxjs/toolkit";

const cartInitialState = {
  listCart: [],
};

const cartSlice = createSlice({
  name: "cart",
  initialState: cartInitialState,
  reducers: {
    addCart(state, payload) {
      if (state.listCart.length === 0) {
        state.listCart.push(payload.payload);
      } else {
        let itemExist = false;
        state.listCart.forEach((cart, index) => {
          if (
            cart.product["_id"]["$oid"] ===
            payload.payload.product["_id"]["$oid"]
          ) {
            itemExist = true;
            state.listCart[index] = {
              product: cart.product,
              quantity:
                Number(cart.quantity) + Number(payload.payload.quantity),
              total: Number(cart.total) + Number(payload.payload.total),
            };
          } else {
          }
        });
        if (itemNotExist) {
          state.listCart.push(payload.payload);
        }
      }

      localStorage.setItem("cartArr", JSON.stringify(state.listCart));
      // else {
      //   state.listCart.map((cart) => {
      //     if (
      //       cart.product["_id"]["$oid"] ===
      //       payload.payload.product["_id"]["$oid"]
      //     ) {
      //       return {
      //         product: cart.product,
      //         quantity: cart.quantity + payload.payload.quantity,
      //         total: cart.total + payload.payload.total,
      //       };
      //     }else {

      //     }
      //   });
      // }
    },
    updateCart(state, payload) {
      state.listCart = state.listCart.map((cart) => {
        if (state.listCart.name === payload.payload.name) {
          return payload.payload;
        } else {
          return cart;
        }
      });
    },
    deleteCart(state, payload) {
      state.listCart = state.listCart.pop(payload.payload);
    },
  },
});

export default cartSlice.reducer;

export const cartActions = cartSlice.actions;
