export function fecthUrl(functionality, params) {
  const serverDomain = "http://localhost:5000";
  let url = serverDomain;
  switch (functionality) {
    case "POST_LOGIN":
      url += "/auth/login";
      break;
    case "POST_SIGNUP":
      url += "/auth/signup";
      break;
    default:
      break;
  }

  return url;
}

export async function fetchData(requestContent) {
  const res = await fetch(requestContent.url, {
    method: requestContent.method ? requestContent.method : "GET",
    body: requestContent.body ? requestContent.body : {},
  });

  if (!res.ok) {
    return await { message: res.statusText, hasError: true };
  } else {
    console.log("hêr");
    return await res.json();
  }
}
