import { Link, Form } from "react-router-dom";
import Card from "../UI/Card";
import backgroundImage from "../../images/banner1.jpg";
import styles from "./SignupForm.module.css";
import { useDispatch, useSelector } from "react-redux";
import { authenticateActions } from "../../store/authenticateSlice";
import { useRef } from "react";

const SignupForm = () => {
  const dispatch = useDispatch();
  const isValid = useSelector((state) => state.authenticate.isValid);
  const nameRef = useRef();
  return (
    <>
      <div
        className={`py-5 ${styles["signup-container"]}`}
        style={{
          backgroundImage: `url(${backgroundImage})`,
          backgroundSize: "cover",
        }}
      >
        <div
          className={`container d-flex align-items-center justify-content-center`}
        >
          <Card className={`py-5 w-50 text-center ${styles["form"]}`}>
            <h2 className={`py-3 ${styles["form-header"]}`}>Sign Up</h2>
            <Form method="post" className="px-5">
              <div className="my-3">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Full Name"
                  name="full-name"
                  ref={nameRef}
                  onFocus={dispatch(
                    authenticateActions.fullnameValidation({
                      value: nameRef.current.value,
                      isTouched: true,
                    })
                  )}
                  onBlur={() =>
                    dispatch(
                      authenticateActions.fullnameValidation({
                        value: nameRef.current.value,
                        isTouched: true,
                      })
                    )
                  }
                />
                {!isValid && <p>Name is not valid</p>}
              </div>
              <div className="my-3">
                <input
                  type="email"
                  className="form-control"
                  placeholder="Email"
                  name="email"
                />
              </div>
              <div className="my-3">
                <input
                  type="password"
                  className="form-control"
                  placeholder="Password"
                  name="password"
                />
              </div>
              <div className="my-3">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Phone"
                  name="phone"
                />
              </div>
              <div className={styles["btn-signup"]}>
                <button className={`btn py-2 d-block w-100 my-5`}>
                  Sign up
                </button>
              </div>
              <p className={styles["navigate"]}>
                Login? <Link>Click</Link>
              </p>
            </Form>
          </Card>
        </div>
      </div>
    </>
  );
};

export default SignupForm;

export async function action({ request, params }) {
  const req = await request.formData();
  const user = {
    name: req.get("full-name"),
    email: req.get("email"),
    password: req.get("password"),
    phone: req.get("phone"),
  };
  console.log(user);

  return user;
}
