import Card from "../UI/Card";
import styles from "./LiveChatPopup.module.css";
import { useSelector } from "react-redux";
import openSocket from "socket.io-client";
import { fetchUrl, fetchData } from "../../helper/fetchUrl";
import { getUser } from "../../helper/getUser";
import { useEffect, useState } from "react";

const LiveChatPopup = () => {
  const user = getUser();
  const isDisplay = useSelector((state) => state.liveChat.isDisplay);
  const [roomId, setRoomId] = useState(null);
  const [messages, setMessages] = useState([]);
  const [error, setError] = useState(null);

  const socket = openSocket(fetchUrl("SERVER_DOMAIN"), {
    transports: ["websocket"],
  });

  // const getChatRoom = async () => {
  //   const res = await fetchData({
  //     url: fetchUrl("GET_CHAT_ROOM"),
  //     method: "POST",
  //     body: JSON.stringify({
  //       email: user.email,
  //       password: user.password,
  //       role: user.role,
  //     }),
  //     headers: { "Content-Type": "application/json" },
  //   });

  //   if (res.hasError) {
  //     setError("Can't get messages");
  //   } else {
  //     setError(null);

  //     setMessages([...res.chatRoom]);
  //   }
  // };

  useEffect(() => {
    // getChatRoom();

    socket.on("chat", (c) => {
      if (c.action === "server") {
        setMessages([...c.messages]);
        setRoomId(c.roomId);
      }
    });
  }, []);

  const clientChatHandler = (e) => {
    e.preventDefault();
    const message = "client:" + e.target.message.value;
    setMessages([message]);

    socket.emit("chat", {
      action: "client",
      message: message,
      // user: user,
      roomId: roomId,
    });
  };

  return (
    <>
      <Card
        className={`p-4 ${styles["popup"]} ${!isDisplay && styles["hidden"]}`}
      >
        <div>
          <header className="d-flex gap-5 align-items-center justify-content-between pb-4 border-bottom border-warning">
            <div>
              <p className={`${styles["supporter-name"]} mb-0`}>
                Customer Support
              </p>
            </div>
          </header>

          <main className={styles["main"]}>
            <div className="my-3 d-flex flex-column gap-3">
              {error ? (
                <p className="text-danger">{error}</p>
              ) : (
                <>
                  {messages.map((message) => (
                    <div
                      id="chat-box"
                      key={message._id}
                      className={`${styles["msg"]} ${
                        message.userRole === "client"
                          ? styles["msg-user"]
                          : styles["msg-admin"]
                      }`}
                    >
                      <p className="p-2 mb-0">{message.message}</p>
                    </div>
                  ))}
                </>
              )}
            </div>
          </main>

          <form className="input-group mt-2" onSubmit={clientChatHandler}>
            <input
              type="text"
              className="form-control"
              name="message"
              placeholder="Enter your message..."
            />
            <button className="btn btn-warning">Send</button>
          </form>
        </div>
      </Card>
    </>
  );
};

export default LiveChatPopup;
