import { createSlice } from "@reduxjs/toolkit";

const authenticateInitialState = {
  isValid: true,
  isTouched: false,
};

const authenticateSlice = createSlice({
  name: "authenticate",
  initialState: authenticateInitialState,
  reducers: {
    setIstouched(state) {
      state.isTouched = true;
    },
    fullnameValidation(state, payload) {
      console.log(payload.payload);
      if (payload.payload.trim().includes("")) {
        state.isValid = false;
        state.isTouched = false;
      } else {
        state.isValid = true;
        state.isTouched = false;
      }
    },
    emailValidation(state, payload) {
      // const emailList = JSON.parse(localStorage.getItem("userArr"));

      payload.payload.includes("@") || payload.payload.trim().includes("")
        ? (state.isValid = true)
        : (state.isValid = false);
    },
    passwordValidation(state, payload) {
      payload.payload.length < 9
        ? (state.isValid = false)
        : (state.isValid = true);
    },
    phoneValidation(state, payload) {
      payload.payload.length > 12
        ? (state.isValid = false)
        : (state.isValid = true);
    },
  },
});

export default authenticateSlice.reducer;

export const authenticateActions = authenticateSlice.actions;
