import { useState, memo } from "react";
import ProductItem from "./ProductItem";
import { useSelector } from "react-redux";
import styles from "./ShopProductsList.module.css";

const ShopProductsList = memo(({ products }) => {
  const segments = getSegments(products, products.length);
  const currentPage = useSelector((state) => state.pagenation.currentPage);

  const firstSixItems = segments[currentPage - 1];

  const firstLineItems = firstSixItems.filter(
    (product, index) => index >= 0 && index <= 2
  );

  const secondLineItems = firstSixItems.filter(
    (product, index) => index >= 3 && index <= 5
  );

  return (
    <>
      {firstSixItems && (
        <div className="d-flex flex-column">
          <div className="d-flex">
            {firstLineItems.map((product) => (
              <ProductItem key={product["_id"]["$oid"]} product={product} />
            ))}
          </div>
          <div className="d-flex">
            {secondLineItems.map((product) => (
              <ProductItem key={product["_id"]["$oid"]} product={product} />
            ))}
          </div>
          {pagenation(products.length, currentPage, segments, setCurrentPage)}
        </div>
      )}
    </>
  );
});

export default ShopProductsList;

function getSegments(products, totalResults) {
  const temp = [...products];
  const totalSegments = Math.ceil(totalResults / 6);
  const segmentArr = [];
  const allSegmentArr = [];
  for (let i = 0; i < totalSegments; i++) {
    for (let j = 0; j < 6; j++) {
      if (temp.length === 0) {
        break;
      }
      segmentArr.push(temp.shift());
    }
    allSegmentArr.push([...segmentArr]);
    segmentArr.splice(0, segmentArr.length);
  }
  console.log(allSegmentArr);

  return allSegmentArr;
}

// Pagenation function
function pagenation(totalResults, currentPage, segments, setCurrentPage) {
  let pages = Math.ceil(totalResults / 6);
  let first = false;
  let last = false;

  let space = [];
  if (currentPage === 1 || currentPage === 2 || currentPage === 3) {
    space = [1, 2, 3, 4, 5];
  } else if (
    currentPage === pages ||
    currentPage === pages - 1 ||
    currentPage === pages - 2
  ) {
    space = [pages - 4, pages - 3, pages - 2, pages - 1, pages];
  } else {
    space = [
      currentPage - 2,
      currentPage - 1,
      currentPage,
      currentPage + 1,
      currentPage + 2,
    ];
  }

  if (currentPage === 1) {
    first = true;
  }

  if (currentPage === pages) {
    last = true;
  }

  return (
    <>
      <ul>
        <li className={`${first === true ? styles["hidden"] : ""}`}>
          <button
            className="page-link"
            href="#"
            id="btn-prev"
            // onClick="clickBtnPage(true)"
          >
            Previous
          </button>
        </li>

        {segments.map((s, i) => (
          <li
            key={segments[i]}
            className={` ${space[i] === currentPage ? styles["disable"] : ""}`}
          >
            <button
              className="page-link"
              id="page-num"
              onClick={() => setCurrentPage(i)}
            >
              {space[i]}
            </button>
          </li>
        ))}

        <li className={`page-item ${last === true ? styles["hidden"] : ""}`}>
          <button
            className="page-link"
            id="btn-next"
            // onClick="clickBtnPage(false)"
          >
            Next
          </button>
        </li>
      </ul>
    </>
  );
}
