import { Link } from "react-router-dom";

const Banner = () => {
  const backgroundImage = ".src/images/banner1.jpg";

  return (
    <>
      <div className="container py-4">
        <div
          style={{
            backgroundImage: `linear-gradient(to bottom, rgba(163, 158, 158, 0.4), rgba(0, 0, 0.6, 0.73)),
          url(${backgroundImage}`,
          }}
        >
          <h3>New Inspiration 2020</h3>
          <p>20% off on new season</p>
          <Link to="/shop">Browse collections</Link>
        </div>
      </div>
    </>
  );
};

export default Banner;
