import { useSelector, useDispatch } from "react-redux";
import { pagenationActions } from "../../store/pagenationSlice";
import styles from "./Pagenation.module.css";

const paging = (totalResults) => {
  const pages = Math.ceil(totalResults / 6);
  let first = false;
  let last = false;

  const space = [];
  if (currentPage === 1 || currentPage === 2 || currentPage === 3) {
    space = [1, 2, 3, 4, 5];
  } else if (
    currentPage === pages ||
    currentPage === pages - 1 ||
    currentPage === pages - 2
  ) {
    space = [pages - 4, pages - 3, pages - 2, pages - 1, pages];
  } else {
    space = [
      currentPage - 2,
      currentPage - 1,
      currentPage,
      currentPage + 1,
      currentPage + 2,
    ];
  }

  if (currentPage === 1) {
    first = true;
  }

  if (currentPage === pages) {
    last = true;
  }

  return { space, first, last };
};

const Pagenation = ({ totalResults, segments }) => {
  const currentPage = useSelector((state) => state.pagenation.currentPage);
  const dispatch = useDispatch();
  const { space, first, last } = paging(totalResults);

  return (
    <>
      <ul>
        <li className={`${first === true ? styles["hidden"] : ""}`}>
          <button
            className="page-link"
            href="#"
            id="btn-prev"
            // onClick="clickBtnPage(true)"
          >
            Previous
          </button>
        </li>

        {segments.map((s, i) => (
          <li
            key={segments[i]}
            className={` ${space[i] === currentPage ? styles["disable"] : ""}`}
          >
            <button
              className="page-link"
              id="page-num"
              onClick={() => dispatch(pagenationActions.setCurrentPage(i))}
            >
              {space[i]}
            </button>
          </li>
        ))}

        <li className={`page-item ${last === true ? styles["hidden"] : ""}`}>
          <button
            className="page-link"
            id="btn-next"
            // onClick="clickBtnPage(false)"
          >
            Next
          </button>
        </li>
      </ul>
    </>
  );
};

export default Pagenation;
