import { useParams, useRouteLoaderData } from "react-router-dom";

const ProductDetail = () => {
  const params = useParams();
  const productId = params.productId;
  const data = useRouteLoaderData("root");
  const productDetail = data.filter(
    (product) => product["_id"]["$oid"] === productId
  );
  console.log(productDetail[0]);

  return (
    <>
      <div className="container">
        <div>
          <picture>
            <img
              src={productDetail[0].img1}
              className="image-fuild"
              alt="Product Detail 1"
            ></img>
            <img
              src={productDetail[0].img2}
              className="image-fuild"
              alt="Product Detail 2"
            ></img>
            <img
              src={productDetail[0].img3}
              className="image-fuild"
              alt="Product Detail 3"
            ></img>
            <img
              src={productDetail[0].img4}
              className="image-fuild"
              alt="Product Detail 4"
            ></img>
          </picture>
          <picture>
            <img src={productDetail[0].img1} alt="Product Detail 1"></img>
          </picture>
          <div></div>
        </div>
      </div>
    </>
  );
};

export default ProductDetail;
